import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import styles from '../styles/Quiz.module.css';

function Quiz() {
  const { questions, currentQuestionIndex } = useSelector((state) => state.quiz);
  const dispatch = useDispatch();

  if (!questions.length) {
    return <div>Loading...</div>;
  }

  const question = questions[currentQuestionIndex];

  const handleAnswer = (option) => {
    const isCorrect = option === question.correctOption; // Adjust based on your data
    dispatch({ type: 'SUBMIT_ANSWER', payload: { questionId: question.id, isCorrect } });
  };

  return (
    <div className={styles.quizContainer}>
      <h2>{question.question}</h2>
      {question.options.map((option) => (
        <button key={option} onClick={() => handleAnswer(option)} className={styles.optionButton}>
          {option}
        </button>
      ))}
    </div>
  );
}

export default Quiz;
