// src/redux/reducers/quizReducer.js
const initialState = {
    questions: [],
  };
  
  const quizReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'SET_QUESTIONS':
        return {
          ...state,
          questions: action.payload,
        };
      default:
        return state;
    }
  };
  
  export default quizReducer;
