// store.js
import { createStore, applyMiddleware, combineReducers } from 'redux';
import thunk from 'redux-thunk'; // Import thunk as a named import
import quizReducer from './reducers/quizReducer';  // Adjust the path according to your folder structure

// Combine your reducers
const rootReducer = combineReducers({
  quiz: quizReducer,
  // Add other reducers here if needed
});

// Create the Redux store with `redux-thunk` middleware
const store = createStore(rootReducer, applyMiddleware(thunk));

export default store;
