// selectors.js
import { createSelector } from 'reselect';

// Input selector: selects the `questions` state from the `quiz` state.
const selectQuestionsState = (state) => state.quiz.questions || [];

// Memoized selector: returns the questions array.
export const getQuestions = createSelector(
  [selectQuestionsState],
  (questions) => questions
);
