import { createStore, applyMiddleware, combineReducers } from 'redux';
import 'redux-thunk'; // This is correct, no curly braces needed
import quizReducer from './reducers/quizReducer'; // Adjust the path as per your project structure

// Combine all your reducers
const rootReducer = combineReducers({
  quiz: quizReducer,
  // Add other reducers here if necessary
});

// Create the Redux store with the rootReducer and apply the redux-thunk middleware
const store = createStore(rootReducer, applyMiddleware(thunk));

export default store;
