// reducers.js
const initialState = {
  score: 0,
  totalQuestions: 4,
};

const quizReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'SET_ANSWER':
      return {
        ...state,
        score: state.score + (action.payload ? 1 : 0),
      };
    case 'RESET_SCORE':
      return {
        ...state,
        score: 0,
      };
    default:
      return state;
  }
};

export default quizReducer;
