import { createSelector } from 'reselect';

const selectQuizState = (state) => state.quiz || {};  // Provide a fallback empty object

export const selectQuestionsState = createSelector(
  [selectQuizState],
  (quizState) => quizState.questions || []  // Provide a fallback empty array
);
