const initialState = {
  questions: [],  // Initialize as an empty array
  currentQuestionIndex: 0,
  // other state properties...
};

function quizReducer(state = initialState, action) {
  switch (action.type) {
    case 'SET_QUESTIONS':
      return {
        ...state,
        questions: action.payload,
      };
    case 'SUBMIT_ANSWER':
      // handle answer submission
      return state;
    default:
      return state;
  }
}
